import{_ as n,o as i,c as h,e as t}from"./app.bb909b48.js";const c={},a=t('<h1 id="lecture-1" tabindex="-1"><a class="header-anchor" href="#lecture-1" aria-hidden="true">#</a> Lecture 1</h1><h2 id="triet-hoc-la-gi" tabindex="-1"><a class="header-anchor" href="#triet-hoc-la-gi" aria-hidden="true">#</a> Triết học là gì</h2><blockquote><p>Triết học là hệ thống tri thức lý luận <span style="color:red;">chung nhất</span> của con người về thế giới; về vị trí, vai trò của <span style="color:red;">con người</span> trong thế giới ấy</p></blockquote><h2 id="i-triet-hoc-mac-lenin" tabindex="-1"><a class="header-anchor" href="#i-triet-hoc-mac-lenin" aria-hidden="true">#</a> I - Triết học Mac Lênin</h2><h3 id="_1-su-ra-đoi" tabindex="-1"><a class="header-anchor" href="#_1-su-ra-đoi" aria-hidden="true">#</a> 1. Sự ra đời</h3><h4 id="a-tien-đe-khach-quan" tabindex="-1"><a class="header-anchor" href="#a-tien-đe-khach-quan" aria-hidden="true">#</a> a. Tiền đề khách quan</h4><ul><li>Nội dung: <ul><li>Điều kiện kinh tế - xã hội: đấu tranh tự phát, đấu tranh giai cấp</li><li>Lí luận: kế thừa triết học cổ điển Đức</li><li>Khoa học: thuyết tiến hoá, thuyết tế bào, định luật bảo toàn và chuyển hoá năng lượng</li></ul></li></ul><h4 id="b-tien-đe-chu-quan" tabindex="-1"><a class="header-anchor" href="#b-tien-đe-chu-quan" aria-hidden="true">#</a> b. Tiền đề chủ quan</h4><ul><li>Nội dung <ul><li>Mác-Awngen có trí tuệ uyên bác, tinh thần tự học</li><li>Tích cực tham gia phong trào công nhân, yêu thương người lao động222</li><li>Tình bạn cao cả, khiêm tốn: Mác - Ăng-gen có một tình bạn cao cả, cùng chí hướng</li></ul></li></ul><h2 id="ii-đoi-tuong-va-chuc-nang-cua-triet-hoc-mac-lenin" tabindex="-1"><a class="header-anchor" href="#ii-đoi-tuong-va-chuc-nang-cua-triet-hoc-mac-lenin" aria-hidden="true">#</a> II - Đối tượng và chức năng của triết học Mác-Lênin</h2><ul><li>Nghĩa rộng: Triết học Mác-Lê nin là hệ thống quan điểm duy vật biện chứng cả về tự nhiên, xã hội và <span style="color:red;"><u>tư duy</u></span></li><li>Nghĩa hẹp: Triết học Mác-Lê nin là là hệ thống thế giới quan, phương pháp luận khoa học và cách mạng, là vũ khí tinh thần của giai cấp công nhân và nhân dân lao dộng để nhận thức và cải tạo thế giới</li></ul><h3 id="_1-khai-niem-triet-hoc-mac-lenin" tabindex="-1"><a class="header-anchor" href="#_1-khai-niem-triet-hoc-mac-lenin" aria-hidden="true">#</a> 1. Khái niệm triết học Mác- Lênin</h3><ul><li>Nội dung: <ul><li>Chủ nghĩa duy vật (CNDV) và phép biện chứng (PBC) thống nhất với nhau thành chủ nghĩa duy vật biện chứng (CNDVBC)</li><li>Là kết quả của sự kế thừa, phát triển tư duy TH nhân loại đồng thời là sự khái quát thành tựu mới cuả khoa học</li><li>Là thế giới quan và phuong pháp luận khoa học của giai cấp công nhân trong nhận thức và cải tạo xã hội</li></ul></li></ul><h3 id="_2-đoi-tuong-cua-triet-hoc-mac-lenin" tabindex="-1"><a class="header-anchor" href="#_2-đoi-tuong-cua-triet-hoc-mac-lenin" aria-hidden="true">#</a> 2. Đối tượng của triết học Mác-Lênin</h3><ul><li>Giải quyết mối quan hệ giữa vật chất và ý thức trên lập trường duy vật biện chứng, và nghiên cứu những quy luật chung nhất của tự nhiên, xã hội và <span style="color:red;"><u>tư duy</u></span></li><li>Vấn đề cơ bản của triết học Mác-Lênin là mối quan hệ giữa vật chất, ý thức và vai trò của con người trong nhận thức và cải tạo thế giới (1 vấn đề nhưng 2 mặt)</li></ul><ul><li>Biểu hiện: <ul><li>Sự thống nhất giữa biện chứng khách quan và biện chứng chủ quan</li><li>Thống nhất giữa chủ nghiã duy vật biện chứng và chủ nghĩa duy vật lịch sử</li><li>Thống nhất giữa phép biện chứng - lý luận nhận thức - logic học</li></ul></li><li>Phân biệt <ul><li>Đối tượng triết học Mác- Lênin</li><li>Dối tượng của các ngành khoa học cụ thể</li></ul><table style="border-collapse:collapse;width:100%;border-color:#000;"><tbody><tr><td style="width:12.6948%;text-align:center;"><strong>Tiêu chí</strong></td><td style="width:44.1655%;text-align:center;"><strong>Đối tượng nghiên cứu của triết học</strong></td><td style="width:41.6691%;text-align:center;"><strong>Đối tượng nghiên cứu của các môn khoa học khác</strong></td></tr><tr><td style="width:12.6948%;text-align:center;">Phạm vi</td><td style="width:44.1655%;"><p>Nghiên cứu rộng hơn, những lý luận chung trong cuộc sống con người</p><p>Ví dụ: Nghiên cứu sự lưu thông của tiền tệ, hàng hóa.</p><p>Nghiên cứu sự vận động của xã hội</p></td><td style="width:41.6691%;"><p>Nghiên cứu những lịch vực cụ thể đối với từng ngành khoa học:</p><p>Ví dụ:</p><p>Sinh học: Nghiên cứu sự biến đổi gen</p><p>Hóa học: Nghiên cứu ra các chất khử khuẩn thân thiện với môi trường</p></td></tr><tr><td style="width:12.6948%;text-align:center;">Tính chất</td><td style="width:44.1655%;"><p>Mang tính lý luận, trừu tượng</p><p>Ví dụ: Nghiên cứu mối quan hệ giữa vật chất và ý thức</p></td><td style="width:41.6691%;"><p>Mang tính chính xác, khoa học, có thể biểu hiện thành bảng số liệu,...</p><p>Ví dụ: Nghiên cứu sự tác động của khí cacbonic đến môi trường sống</p></td></tr></tbody></table></li></ul><h3 id="_3-chuc-nang-cua-triet-hoc-mac-lenin" tabindex="-1"><a class="header-anchor" href="#_3-chuc-nang-cua-triet-hoc-mac-lenin" aria-hidden="true">#</a> 3. Chức năng của triết học Mác-Lênin</h3><ul><li>Chức năng thế giới quan <ul><li>Thế giới quan là hệ thống quan điểm chung nhất về thế giới và vai trò vị trí của con người trong thế giới</li><li>Thế giới quan duy vật và thế giới quan duy tâm</li><li>Triết học Mác- Lênin đem lại thế giới quan duy vật biện chứng</li></ul></li><li>Vai trò thế giới quan duy vật biện chứng <ul><li>Trang bị tri thức khoa học nhằm nâng cao nhận thức của con người trong cải tạo thế giới</li><li>Định hướng cho hoạt động của con người trong nhận thức và hoạt động thực tiễn</li><li>Nâng cao tính tích cực sáng tạo của con người trong hoạt động thực tiễn</li><li>Là hạt nhân tư tưởng của giai cấp vô sản</li></ul></li><li>Phương pháp luật <ul><li>Khái niệm <blockquote><p>Phương pháp luật là hệ thống những quan điểm, nguyên tắc xuất phát cho hoạt động nhận thức và thực tiễn</p></blockquote></li><li>Triết học Mác- Lênin <blockquote><p>Là phương pháp luận chung nhất cho nhận thức và thực tiễn</p></blockquote></li></ul></li><li>Vai trò phương pháp luận duy vật biện chứng <ul><li>Là phương pháp luận chung cho toàn bộ nhận thức khao học</li><li>Trang bị cho con người hệ thống những nhận thức, quy tắc nhận thức cho các sự vật hiện tượng của thế giới</li><li>Trang bị cho con người hệ thống công cụ trong nhận thức khoa học và hoạt động thực tiễn</li><li>Là cơ sở cho các bộ môn khoa học</li></ul></li><li>Mối quan hệ giữa hai chức năng <ul><li>Trong triết học MLN TGQ và PPL luôn thống nhất không tách rời</li><li>TGQ là cơ sở của PPL còn PPL củng cố góp phần TGQ</li></ul></li><li>Vai trò ngày càng tăng của triết học Mác- Lênin <ul><li>Biểu hiện: <ul><li><u>Thời đại</u> ngày nay và những vấn đề cấp bách của nhân loại đòi hỏi nâng cao tư duy triết học</li><li>Cuộc cách mạng khoa học, công nghệ hiện đại phát triển trên thế giới và Việt Nam hiện nay</li><li>Do những yêu cầu cảu công cuộc xây dựng Chủ Nghĩa Xã Hội trên thế giới và sự nghiệp đổi mới ở VN</li></ul></li><li>Kết luận chung <ul><li>Triết học MLN không phải là đơn thuốc vạn năng có thể giải quyết được mọi vấn đề</li><li>Trong nhận thức và HĐTT không được xem thường hoặc tuyệt đối hoá triết học</li><li>Sự cần thiết bồi dưỡng TGQ và PPL duy vật biện chứng giúp cho chúng ta tránh khỏi sai lầm</li></ul></li></ul></li></ul>',18),l=[a];function g(u,o){return i(),h("div",null,l)}const r=n(c,[["render",g],["__file","Lec1.html.vue"]]);export{r as default};
